import React, { useState, useEffect } from 'react';
import { classesAPI, assignmentsAPI } from '../services/api';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart, Pie, Cell, LineChart, Line, ResponsiveContainer } from 'recharts';
import '../styles/dashboard.css';

const Laporan = () => {
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState('');
  const [classStats, setClassStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [statsLoading, setStatsLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTeacherClasses();
  }, []);

  const fetchTeacherClasses = async () => {
    try {
      setLoading(true);
      const response = await classesAPI.getTeacherClasses();
      setClasses(response.data);
    } catch (error) {
      setError('Gagal memuat kelas');
      console.error('Error fetching classes:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchClassStats = async (classId) => {
    try {
      setStatsLoading(true);
      const response = await assignmentsAPI.getClassStats(classId);
      setClassStats(response.data);
    } catch (error) {
      console.error('Error fetching class stats:', error);
      setError('Gagal memuat statistik kelas');
    } finally {
      setStatsLoading(false);
    }
  };

  const handleClassChange = (e) => {
    const classId = e.target.value;
    setSelectedClass(classId);
    if (classId) {
      fetchClassStats(classId);
    } else {
      setClassStats(null);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Memuat laporan...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Laporan</h1>
        <p>Analisis dan statistik pembelajaran</p>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="filter-section">
        <label htmlFor="classSelect">Pilih Kelas:</label>
        <select 
          id="classSelect" 
          value={selectedClass} 
          onChange={handleClassChange}
          className="filter-select"
        >
          <option value="">-- Pilih Kelas --</option>
          {classes.map((classItem) => (
            <option key={classItem.id} value={classItem.id}>
              {classItem.nama_kelas} ({classItem.kode_kelas})
            </option>
          ))}
        </select>
      </div>

      {statsLoading && (
        <div className="loading">Memuat statistik...</div>
      )}

      {classStats && (
        <div className="reports-grid">
          {/* Overview Card */}
          <div className="report-card">
            <h3>Overview Kelas</h3>
            <div className="stats-grid">
              <div className="stat-item">
                <div className="stat-number">{classStats.total_students || 0}</div>
                <div className="stat-label">Total Siswa</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">{classStats.total_assignments || 0}</div>
                <div className="stat-label">Total Tugas</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">{classStats.total_materials || 0}</div>
                <div className="stat-label">Total Materi</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">{classStats.avg_submission_rate || 0}%</div>
                <div className="stat-label">Rata-rata Pengumpulan</div>
              </div>
            </div>
          </div>

          {/* Assignment Stats */}
          <div className="report-card">
            <h3>Statistik Tugas</h3>
            {classStats.assignments && classStats.assignments.length > 0 ? (
              <div className="assignment-stats">
                {classStats.assignments.map((assignment) => {
                  const submissionRate = assignment.total_students > 0 
                    ? Math.round((assignment.submitted_count / assignment.total_students) * 100)
                    : 0;

                  return (
                    <div key={assignment.id} className="assignment-stat-item">
                      <h4>{assignment.judul}</h4>
                      <div className="progress-section">
                        <span>{assignment.submitted_count}/{assignment.total_students} siswa</span>
                        <div className="progress-bar">
                          <div 
                            className="progress-fill" 
                            style={{ width: `${submissionRate}%` }}
                          ></div>
                        </div>
                        <span className="percentage">{submissionRate}%</span>
                      </div>
                      <div className="deadline-info">
                        Deadline: {new Date(assignment.deadline).toLocaleDateString('id-ID')}
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <p className="no-data">Belum ada tugas untuk kelas ini</p>
            )}
          </div>

          {/* Student Performance */}
          <div className="report-card">
            <h3>Performance Siswa</h3>
            {classStats.students && classStats.students.length > 0 ? (
              <div className="student-performance">
                <table className="performance-table">
                  <thead>
                    <tr>
                      <th>Nama Siswa</th>
                      <th>Tugas Diserahkan</th>
                      <th>Persentase</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {classStats.students.map((student) => {
                      const completionRate = classStats.total_assignments > 0
                        ? Math.round((student.submitted_assignments / classStats.total_assignments) * 100)
                        : 0;

                      const status = completionRate >= 80 ? 'Excellent' :
                                    completionRate >= 60 ? 'Good' :
                                    completionRate >= 40 ? 'Fair' : 'Poor';

                      return (
                        <tr key={student.id}>
                          <td>{student.nama_lengkap}</td>
                          <td>{student.submitted_assignments}/{classStats.total_assignments}</td>
                          <td>{completionRate}%</td>
                          <td>
                            <span className={`status-badge ${status.toLowerCase()}`}>
                              {status}
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="no-data">Belum ada data siswa</p>
            )}
          </div>

          {/* Material Engagement */}
          <div className="report-card">
            <h3>Engagement Materi</h3>
            {classStats.materials && classStats.materials.length > 0 ? (
              <div className="material-engagement">
                {classStats.materials.map((material) => (
                  <div key={material.id} className="material-stat-item">
                    <h4>{material.judul}</h4>
                    <div className="engagement-info">
                      <span>Diakses: {material.access_count || 0} kali</span>
                      <span>Download: {material.download_count || 0} kali</span>
                    </div>
                    <div className="material-date">
                      Dibuat: {new Date(material.created_at).toLocaleDateString('id-ID')}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="no-data">Belum ada materi untuk kelas ini</p>
            )}
          </div>

          {/* Charts Section */}
          {classStats.students && classStats.students.length > 0 && (
            <>
              {/* Student Grade Distribution Chart */}
              <div className="report-card chart-card">
                <h3>Distribusi Nilai Siswa</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={prepareGradeDistributionData(classStats.students)}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="range" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="count" fill="#8884d8" name="Jumlah Siswa" />
                  </BarChart>
                </ResponsiveContainer>
              </div>

              {/* Assignment Completion Pie Chart */}
              <div className="report-card chart-card">
                <h3>Tingkat Pengumpulan Tugas</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <PieChart>
                    <Pie
                      data={prepareCompletionData(classStats.assignments)}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                    >
                      {prepareCompletionData(classStats.assignments).map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip />
                  </PieChart>
                </ResponsiveContainer>
              </div>

              {/* Student Performance Line Chart */}
              <div className="report-card chart-card">
                <h3>Performance Siswa</h3>
                <ResponsiveContainer width="100%" height={300}>
                  <LineChart data={prepareStudentPerformanceData(classStats.students)}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="completionRate" stroke="#8884d8" name="Tingkat Pengumpulan (%)" />
                    {classStats.students.some(s => s.average_grade) && (
                      <Line type="monotone" dataKey="averageGrade" stroke="#82ca9d" name="Rata-rata Nilai" />
                    )}
                  </LineChart>
                </ResponsiveContainer>
              </div>
            </>
          )}
        </div>
      )}

      {!selectedClass && (
        <div className="empty-state">
          <h3>Pilih Kelas</h3>
          <p>Silakan pilih kelas untuk melihat laporan dan statistik</p>
        </div>
      )}
    </div>
  );
};

// Helper functions for chart data preparation
const prepareGradeDistributionData = (students) => {
  const gradeRanges = {
    '0-20': 0,
    '21-40': 0,
    '41-60': 0,
    '61-80': 0,
    '81-100': 0
  };

  students.forEach(student => {
    if (student.average_grade) {
      const grade = parseFloat(student.average_grade);
      if (grade <= 20) gradeRanges['0-20']++;
      else if (grade <= 40) gradeRanges['21-40']++;
      else if (grade <= 60) gradeRanges['41-60']++;
      else if (grade <= 80) gradeRanges['61-80']++;
      else gradeRanges['81-100']++;
    }
  });

  return Object.entries(gradeRanges).map(([range, count]) => ({
    range,
    count
  }));
};

const prepareCompletionData = (assignments) => {
  if (!assignments || assignments.length === 0) return [];

  const totalAssignments = assignments.length;
  let completedCount = 0;
  let incompleteCount = 0;

  assignments.forEach(assignment => {
    const rate = assignment.total_students > 0
      ? (assignment.submitted_count / assignment.total_students) * 100
      : 0;
    if (rate >= 80) completedCount++;
    else incompleteCount++;
  });

  return [
    { name: 'Tinggi (≥80%)', value: completedCount },
    { name: 'Rendah (<80%)', value: incompleteCount }
  ];
};

const prepareStudentPerformanceData = (students, totalAssignments) => {
  return students.map(student => ({
    name: student.nama_lengkap.split(' ')[0], // Use first name for brevity
    completionRate: students.length > 0 && totalAssignments > 0
      ? Math.round((student.submitted_assignments / totalAssignments) * 100)
      : 0,
    averageGrade: student.average_grade ? parseFloat(student.average_grade) : null
  }));
};

// Colors for pie chart
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

export default Laporan;
