import React, { useState, useEffect } from 'react';
import { materialsAPI } from '../services/api';
import '../styles/dashboard.css';

const ManajemenMateri = () => {
  const [materials, setMaterials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createData, setCreateData] = useState({
    kelas_id: '',
    judul: '',
    deskripsi: '',
    file: null
  });
  const [createLoading, setCreateLoading] = useState(false);
  const [createError, setCreateError] = useState('');

  useEffect(() => {
    fetchMaterials();
  }, []);

  const fetchMaterials = async () => {
    try {
      setLoading(true);
      const response = await materialsAPI.getTeacherMaterials();
      setMaterials(response.data);
    } catch (error) {
      setError('Gagal memuat materi');
      console.error('Error fetching materials:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateMaterial = async (e) => {
    e.preventDefault();
    setCreateLoading(true);
    setCreateError('');

    try {
      const formData = new FormData();
      formData.append('kelas_id', createData.kelas_id);
      formData.append('judul', createData.judul);
      formData.append('deskripsi', createData.deskripsi);
      if (createData.file) {
        formData.append('file', createData.file);
      }

      await materialsAPI.create(formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setShowCreateModal(false);
      setCreateData({ kelas_id: '', judul: '', deskripsi: '', file: null });
      fetchMaterials();
    } catch (error) {
      console.error('Error creating material:', error);
      setCreateError('Gagal membuat materi');
    } finally {
      setCreateLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'file') {
      setCreateData({ ...createData, file: files[0] });
    } else {
      setCreateData({ ...createData, [name]: value });
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Memuat materi...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Manajemen Materi</h1>
        <p>Kelola materi pembelajaran</p>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="action-bar">
        <button 
          className="btn btn-primary"
          onClick={() => setShowCreateModal(true)}
        >
          + Tambah Materi Baru
        </button>
      </div>

      <div className="content-grid">
        {materials.length === 0 ? (
          <div className="empty-state">
            <h3>Belum ada materi</h3>
            <p>Anda belum menambahkan materi apapun.</p>
          </div>
        ) : (
          <div className="cards-grid">
            {materials.map((material) => (
              <div key={material.id} className="card">
                <div className="card-header">
                  <h3>{material.judul}</h3>
                  <span className="material-type">{material.file_type || 'Teks'}</span>
                </div>
                <div className="card-body">
                  <p><strong>Kelas:</strong> {material.nama_kelas}</p>
                  <p><strong>Deskripsi:</strong> {material.deskripsi}</p>
                  <p><strong>Dibuat oleh:</strong> {material.created_by_name}</p>
                  <p><strong>Tanggal:</strong> {new Date(material.created_at).toLocaleDateString('id-ID')}</p>
                </div>
                <div className="card-footer">
                  {material.file_path ? (
                    <a 
                      href={`http://localhost:5000${material.file_path}`} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="btn btn-secondary"
                    >
                      Download Materi
                    </a>
                  ) : (
                    <span className="no-file">Tidak ada file</span>
                  )}
                  <button className="btn btn-primary">
                    Edit Materi
                  </button>
                  <button className="btn btn-danger">
                    Hapus
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Create Material Modal */}
      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Tambah Materi Baru</h2>
            {createError && <div className="error-message">{createError}</div>}
            <form onSubmit={handleCreateMaterial}>
              <div className="form-group">
                <label htmlFor="kelas_id">Pilih Kelas:</label>
                <select 
                  id="kelas_id" 
                  name="kelas_id" 
                  value={createData.kelas_id} 
                  onChange={handleInputChange}
                  required
                >
                  <option value="">-- Pilih Kelas --</option>
                  {/* TODO: Populate kelas options */}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="judul">Judul Materi:</label>
                <input
                  type="text"
                  id="judul"
                  name="judul"
                  value={createData.judul}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="deskripsi">Deskripsi:</label>
                <textarea
                  id="deskripsi"
                  name="deskripsi"
                  value={createData.deskripsi}
                  onChange={handleInputChange}
                  rows="3"
                />
              </div>
              <div className="form-group">
                <label htmlFor="file">Upload File:</label>
                <input
                  type="file"
                  id="file"
                  name="file"
                  onChange={handleInputChange}
                />
              </div>
              <div className="modal-actions">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowCreateModal(false)}
                >
                  Batal
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={createLoading}
                >
                  {createLoading ? 'Menyimpan...' : 'Simpan'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManajemenMateri;
