import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';
import '../styles/dashboard.css';

const AdminUserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [pagination, setPagination] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [createData, setCreateData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'siswa',
    nama_lengkap: '',
    kelas: ''
  });
  const [editData, setEditData] = useState({
    role: ''
  });
  const [createLoading, setCreateLoading] = useState(false);
  const [editLoading, setEditLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [createError, setCreateError] = useState('');
  const [editError, setEditError] = useState('');

  useEffect(() => {
    fetchUsers(currentPage);
  }, [currentPage]);

  const fetchUsers = async (page = 1) => {
    try {
      setLoading(true);
      const response = await adminAPI.getAllUsers(page);
      setUsers(response.data.users);
      setPagination(response.data.pagination);
    } catch (error) {
      setError('Gagal memuat data pengguna');
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    setCreateLoading(true);
    setCreateError('');

    try {
      await adminAPI.createUser(createData);
      setShowCreateModal(false);
      setCreateData({
        username: '',
        email: '',
        password: '',
        role: 'siswa',
        nama_lengkap: '',
        kelas: ''
      });
      fetchUsers(currentPage);
    } catch (error) {
      console.error('Error creating user:', error);
      if (error.response?.data?.error) {
        setCreateError(error.response.data.error);
      } else {
        setCreateError('Gagal membuat pengguna');
      }
    } finally {
      setCreateLoading(false);
    }
  };

  const handleUpdateUserRole = async (e) => {
    e.preventDefault();
    setEditLoading(true);
    setEditError('');

    try {
      await adminAPI.updateUserRole(selectedUser.id, editData.role);
      setShowEditModal(false);
      setSelectedUser(null);
      setEditData({ role: '' });
      fetchUsers(currentPage);
    } catch (error) {
      console.error('Error updating user role:', error);
      if (error.response?.data?.error) {
        setEditError(error.response.data.error);
      } else {
        setEditError('Gagal mengupdate role pengguna');
      }
    } finally {
      setEditLoading(false);
    }
  };

  const handleDeleteUser = async () => {
    setDeleteLoading(true);

    try {
      await adminAPI.deleteUser(selectedUser.id);
      setShowDeleteConfirm(false);
      setSelectedUser(null);
      fetchUsers(currentPage);
    } catch (error) {
      console.error('Error deleting user:', error);
      alert('Gagal menghapus pengguna');
    } finally {
      setDeleteLoading(false);
    }
  };

  const handleEdit = (user) => {
    setSelectedUser(user);
    setEditData({ role: user.role });
    setShowEditModal(true);
  };

  const handleDelete = (user) => {
    setSelectedUser(user);
    setShowDeleteConfirm(true);
  };

  const handleInputChange = (e) => {
    setCreateData({
      ...createData,
      [e.target.name]: e.target.value
    });
  };

  const handleEditInputChange = (e) => {
    setEditData({
      ...editData,
      [e.target.name]: e.target.value
    });
  };

  const getRoleDisplayName = (role) => {
    switch (role) {
      case 'guru':
        return 'Guru';
      case 'admin':
        return 'Admin';
      case 'siswa':
        return 'Siswa';
      default:
        return role;
    }
  };

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'guru':
        return 'role-badge guru';
      case 'admin':
        return 'role-badge admin';
      case 'siswa':
        return 'role-badge siswa';
      default:
        return 'role-badge';
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Memuat pengguna...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Manajemen Pengguna</h1>
        <p>Kelola akun pengguna sistem</p>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="action-bar">
        <button
          className="btn btn-primary"
          onClick={() => setShowCreateModal(true)}
        >
          + Buat Pengguna Baru
        </button>
      </div>

      <div className="content-grid">
        {users.length === 0 ? (
          <div className="empty-state">
            <h3>Belum ada pengguna</h3>
            <p>Belum ada pengguna terdaftar di sistem.</p>
          </div>
        ) : (
          <div className="users-table-container">
            <table className="users-table">
              <thead>
                <tr>
                  <th>Nama Lengkap</th>
                  <th>Username</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Kelas</th>
                  <th>Dibuat</th>
                  <th>Aksi</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user.id}>
                    <td>{user.nama_lengkap}</td>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>
                      <span className={getRoleBadgeClass(user.role)}>
                        {getRoleDisplayName(user.role)}
                      </span>
                    </td>
                    <td>{user.kelas || '-'}</td>
                    <td>{new Date(user.created_at).toLocaleDateString('id-ID')}</td>
                    <td>
                      <button
                        className="btn btn-secondary btn-small"
                        onClick={() => handleEdit(user)}
                      >
                        Edit Role
                      </button>
                      <button
                        className="btn btn-danger btn-small"
                        onClick={() => handleDelete(user)}
                      >
                        Hapus
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination */}
            {pagination.totalPages > 1 && (
              <div className="pagination">
                <button
                  className="btn btn-secondary"
                  onClick={() => setCurrentPage(currentPage - 1)}
                  disabled={currentPage === 1}
                >
                  Sebelumnya
                </button>
                <span>Halaman {currentPage} dari {pagination.totalPages}</span>
                <button
                  className="btn btn-secondary"
                  onClick={() => setCurrentPage(currentPage + 1)}
                  disabled={currentPage === pagination.totalPages}
                >
                  Selanjutnya
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Create User Modal */}
      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Buat Pengguna Baru</h2>
            {createError && <div className="error-message">{createError}</div>}
            <form onSubmit={handleCreateUser}>
              <div className="form-group">
                <label htmlFor="nama_lengkap">Nama Lengkap:</label>
                <input
                  type="text"
                  id="nama_lengkap"
                  name="nama_lengkap"
                  value={createData.nama_lengkap}
                  onChange={handleInputChange}
                  required
                  placeholder="Masukkan nama lengkap"
                />
              </div>
              <div className="form-group">
                <label htmlFor="username">Username:</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={createData.username}
                  onChange={handleInputChange}
                  required
                  placeholder="Masukkan username"
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={createData.email}
                  onChange={handleInputChange}
                  required
                  placeholder="Masukkan email"
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={createData.password}
                  onChange={handleInputChange}
                  required
                  placeholder="Masukkan password"
                />
              </div>
              <div className="form-group">
                <label htmlFor="role">Role:</label>
                <select
                  id="role"
                  name="role"
                  value={createData.role}
                  onChange={handleInputChange}
                  required
                >
                  <option value="siswa">Siswa</option>
                  <option value="guru">Guru</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="kelas">Kelas (untuk siswa):</label>
                <input
                  type="text"
                  id="kelas"
                  name="kelas"
                  value={createData.kelas}
                  onChange={handleInputChange}
                  placeholder="Contoh: X IPA 1"
                />
              </div>
              <div className="modal-actions">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowCreateModal(false)}
                >
                  Batal
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={createLoading}
                >
                  {createLoading ? 'Membuat...' : 'Buat Pengguna'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Edit User Role Modal */}
      {showEditModal && selectedUser && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Edit Role Pengguna</h2>
            <p><strong>Pengguna:</strong> {selectedUser.nama_lengkap} ({selectedUser.username})</p>
            {editError && <div className="error-message">{editError}</div>}
            <form onSubmit={handleUpdateUserRole}>
              <div className="form-group">
                <label htmlFor="edit_role">Role:</label>
                <select
                  id="edit_role"
                  name="role"
                  value={editData.role}
                  onChange={handleEditInputChange}
                  required
                >
                  <option value="siswa">Siswa</option>
                  <option value="guru">Guru</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
              <div className="modal-actions">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setShowEditModal(false);
                    setSelectedUser(null);
                  }}
                >
                  Batal
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={editLoading}
                >
                  {editLoading ? 'Mengupdate...' : 'Update Role'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteConfirm && selectedUser && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Konfirmasi Hapus</h2>
            <p>Apakah Anda yakin ingin menghapus pengguna "{selectedUser.nama_lengkap}"?</p>
            <p>Username: {selectedUser.username}</p>
            <p>Semua data terkait pengguna ini akan dihapus permanen.</p>

            <div className="modal-actions">
              <button
                className="btn btn-secondary"
                onClick={() => {
                  setShowDeleteConfirm(false);
                  setSelectedUser(null);
                }}
                disabled={deleteLoading}
              >
                Batal
              </button>
              <button
                className="btn btn-danger"
                onClick={handleDeleteUser}
                disabled={deleteLoading}
              >
                {deleteLoading ? 'Menghapus...' : 'Ya, Hapus'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminUserManagement;
