import React, { useEffect, useState } from 'react';
import { classesAPI, assignmentsAPI } from '../services/api';
import '../styles/profile.css';

const LaporanNilaiTugas = () => {
  const [classes, setClasses] = useState([]);
  const [selectedClassId, setSelectedClassId] = useState('');
  const [assignments, setAssignments] = useState([]);
  const [loadingClasses, setLoadingClasses] = useState(true);
  const [loadingAssignments, setLoadingAssignments] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        setLoadingClasses(true);
        const response = await classesAPI.getStudentRegistrations();
        setClasses(response.data);
        if (response.data.length > 0) {
          setSelectedClassId(response.data[0].kelas_id || response.data[0].id);
        }
      } catch (err) {
        setError('Gagal memuat daftar kelas.');
      } finally {
        setLoadingClasses(false);
      }
    };
    fetchClasses();
  }, []);

  useEffect(() => {
    const fetchAssignments = async () => {
      if (!selectedClassId) {
        setAssignments([]);
        return;
      }
      try {
        setLoadingAssignments(true);
        setError(null);
        const response = await assignmentsAPI.getStudentClassGrades(selectedClassId);
        setAssignments(response.data || []);
      } catch (err) {
        setError('Gagal memuat nilai tugas.');
        setAssignments([]);
      } finally {
        setLoadingAssignments(false);
      }
    };
    fetchAssignments();
  }, [selectedClassId]);

  const handleClassChange = (e) => {
    setSelectedClassId(e.target.value);
  };

  return (
    <div className="laporan-nilai-tugas-container" style={{ maxWidth: 600, margin: '0 auto', padding: 20 }}>
      <h2>Nilai Tugas Siswa</h2>

      {loadingClasses ? (
        <p>Memuat daftar kelas...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <>
          <label htmlFor="class-select" style={{ display: 'block', marginBottom: 8 }}>
            Pilih Kelas:
          </label>
          <select
            id="class-select"
            value={selectedClassId}
            onChange={handleClassChange}
            style={{ padding: 8, width: '100%', marginBottom: 20 }}
          >
            {classes.map((kelas) => (
              <option key={kelas.kelas_id || kelas.id} value={kelas.kelas_id || kelas.id}>
                {kelas.nama_kelas || kelas.namaKelas || kelas.kode_kelas || kelas.kodeKelas || 'Kelas'}
              </option>
            ))}
          </select>

          {loadingAssignments ? (
            <p>Memuat nilai tugas...</p>
          ) : assignments.length === 0 ? (
            <p>Tidak ada nilai tugas untuk kelas ini.</p>
          ) : (
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {assignments.map((assignment) => (
                <li
                  key={assignment.id}
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    padding: '10px 15px',
                    borderBottom: '1px solid #ddd',
                    alignItems: 'center',
                  }}
                >
                  <span style={{ fontWeight: 'bold', color: '#333' }}>{assignment.judul}</span>
                  <span style={{ fontWeight: 'bold', color: '#555' }}>
                    {assignment.nilai !== null && assignment.nilai !== undefined ? assignment.nilai : '-'}
                  </span>
                </li>
              ))}
            </ul>
          )}
        </>
      )}
    </div>
  );
};

export default LaporanNilaiTugas;
