import React, { useState, useEffect } from 'react';
import '../styles/dashboard.css';

const Profil = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    nama_lengkap: '',
    email: '',
    username: '',
    kelas: ''
  });
  const [saveLoading, setSaveLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadUserProfile();
  }, []);

  const loadUserProfile = () => {
    try {
      const userData = JSON.parse(localStorage.getItem('user') || '{}');
      setUser(userData);
      setFormData({
        nama_lengkap: userData.nama_lengkap || '',
        email: userData.email || '',
        username: userData.username || '',
        kelas: userData.kelas || ''
      });
    } catch (error) {
      setError('Gagal memuat profil');
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    setSaveLoading(true);
    setError('');
    setSuccess('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const updatedUser = {
        ...user,
        ...formData
      };
      
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setEditMode(false);
      setSuccess('Profil berhasil diperbarui');
      
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError('Gagal menyimpan profil');
      console.error('Error saving profile:', error);
    } finally {
      setSaveLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Memuat profil...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Profil Saya</h1>
        <p>Kelola informasi profil Anda</p>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      <div className="profile-content">
        <div className="profile-card shadowed-card">
          <div className="profile-header">
            <div className="profile-avatar">
              {user?.nama_lengkap ? user.nama_lengkap.charAt(0).toUpperCase() : 'U'}
            </div>
            <div className="profile-info">
              <h2>{user?.nama_lengkap || 'User'}</h2>
              <p className="profile-role">
                {user?.role === 'guru' ? 'Guru' : 'Siswa'}
              </p>
            </div>
          </div>

          {!editMode ? (
            <div className="profile-details">
              <div className="detail-item">
                <label>Nama Lengkap:</label>
                <span>{user?.nama_lengkap || '-'}</span>
              </div>
              <div className="detail-item">
                <label>Username:</label>
                <span>{user?.username || '-'}</span>
              </div>
              <div className="detail-item">
                <label>Email:</label>
                <span>{user?.email || '-'}</span>
              </div>
              {user?.role === 'siswa' && (
                <div className="detail-item">
                  <label>Kelas:</label>
                  <span>{user?.kelas || '-'}</span>
                </div>
              )}
              <div className="detail-item">
                <label>Bergabung sejak:</label>
                <span>{user?.created_at ? new Date(user.created_at).toLocaleDateString('id-ID') : '-'}</span>
              </div>
            </div>
          ) : (
            <form onSubmit={handleSaveProfile} className="profile-form">
        <div className="profile-main">
                <label htmlFor="nama_lengkap">Nama Lengkap:</label>
                <input
                  type="text"
                  id="nama_lengkap"
                  name="nama_lengkap"
                  value={formData.nama_lengkap}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="username">Username:</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              {user?.role === 'siswa' && (
                <div className="form-group">
                  <label htmlFor="kelas">Kelas:</label>
                  <input
                    type="text"
                    id="kelas"
                    name="kelas"
                    value={formData.kelas}
                    onChange={handleInputChange}
                    placeholder="Contoh: X IPA 1"
                  />
                </div>
              )}
            </form>
          )}

          <div className="profile-actions">
            {!editMode ? (
              <button
                className="btn btn-primary"
                onClick={() => setEditMode(true)}
              >
                Edit Profil
              </button>
            ) : (
              <div className="edit-actions">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setEditMode(false);
                    loadUserProfile();
                  }}
                >
                  Batal
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  onClick={handleSaveProfile}
                  disabled={saveLoading}
                >
                  {saveLoading ? 'Menyimpan...' : 'Simpan Perubahan'}
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="profile-stats shadowed-card">
          <h3>Statistik Akun</h3>
          <div className="stats-grid">
            <div className="stat-item">
              <div className="stat-number">0</div>
              <div className="stat-label">Kelas Diikuti</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">0</div>
              <div className="stat-label">Tugas Diselesaikan</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">0</div>
              <div className="stat-label">Materi Dipelajari</div>
