import React, { useState, useEffect } from 'react';
import '../styles/dashboard.css';
import '../styles/profile.css';

const Profil = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    nama_lengkap: '',
    email: '',
    username: '',
    kelas: ''
  });
  const [saveLoading, setSaveLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [animatedStats, setAnimatedStats] = useState({
    classes: 0,
    assignments: 0,
    materials: 0,
    activeDays: 0
  });

  useEffect(() => {
    loadUserProfile();
    // Animate stats on load
    setTimeout(() => {
      setAnimatedStats({
        classes: 5,
        assignments: 12,
        materials: 8,
        activeDays: 24
      });
    }, 1000);
  }, []);

  const loadUserProfile = () => {
    try {
      const userData = JSON.parse(localStorage.getItem('user') || '{}');
      setUser(userData);
      setFormData({
        nama_lengkap: userData.nama_lengkap || '',
        email: userData.email || '',
        username: userData.username || '',
        kelas: userData.kelas || ''
      });
    } catch (error) {
      setError('Gagal memuat profil');
      console.error('Error loading profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSaveProfile = async (e) => {
    e.preventDefault();
    setSaveLoading(true);
    setError('');
    setSuccess('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      const updatedUser = {
        ...user,
        ...formData
      };

      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setEditMode(false);
      setSuccess('Profil berhasil diperbarui');

      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError('Gagal menyimpan profil');
      console.error('Error saving profile:', error);
    } finally {
      setSaveLoading(false);
    }
  };

  const getInitials = (name) => {
    if (!name) return 'U';
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'guru': return '#10b981';
      case 'siswa': return '#3b82f6';
      default: return '#6b7280';
    }
  };

  const getRoleIcon = (role) => {
    switch (role) {
      case 'guru': return 'üë®‚Äçüè´';
      case 'siswa': return 'üë®‚Äçüéì';
      default: return 'üë§';
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Memuat profil...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Profil Saya</h1>
        <p>Kelola informasi profil Anda</p>
      </div>

      {error && <div className="error-message animated">{error}</div>}
      {success && <div className="success-message animated">{success}</div>}

      <div className="profile-cards-grid">
        {/* Profile Card */}
        <div className="profile-main-card interactive-card">
          <div className="card-header">
            <div className="profile-avatar-container">
              <div className="profile-avatar animated">
                {getInitials(user?.nama_lengkap)}
              </div>
              <div className="avatar-badge">
                {getRoleIcon(user?.role)}
              </div>
            </div>
            <div className="profile-info">
              <h2 className="animated-text">{user?.nama_lengkap || 'User'}</h2>
              <div className="role-badge" style={{ backgroundColor: getRoleColor(user?.role) }}>
                {user?.role === 'guru' ? 'Guru' : 'Siswa'}
              </div>
            </div>
          </div>

          {!editMode ? (
            <div className="profile-details">
              <div className="detail-grid">
                <div className="detail-card">
                  <div className="detail-icon">üìß</div>
                  <div className="detail-content">
                    <label>Email</label>
                    <span>{user?.email || '-'}</span>
                  </div>
                </div>
                <div className="detail-card">
                  <div className="detail-icon">üë§</div>
                  <div className="detail-content">
                    <label>Username</label>
                    <span>{user?.username || '-'}</span>
                  </div>
                </div>
                {user?.role === 'siswa' && (
                  <div className="detail-card">
                    <div className="detail-icon">üè´</div>
                    <div className="detail-content">
                      <label>Kelas</label>
                      <span>{user?.kelas || '-'}</span>
                    </div>
                  </div>
                )}
                <div className="detail-card">
                  <div className="detail-icon">üìÖ</div>
                  <div className="detail-content">
                    <label>Bergabung sejak</label>
                    <span>{user?.created_at ? new Date(user.created_at).toLocaleDateString('id-ID') : '-'}</span>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <form onSubmit={handleSaveProfile} className="profile-form animated">
              <div className="form-grid">
                <div className="form-group">
                  <label htmlFor="nama_lengkap">Nama Lengkap</label>
                  <input
                    type="text"
                    id="nama_lengkap"
                    name="nama_lengkap"
                    value={formData.nama_lengkap}
                    onChange={handleInputChange}
                    required
                    className="animated-input"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleInputChange}
                    required
                    className="animated-input"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    className="animated-input"
                  />
                </div>
                {user?.role === 'siswa' && (
                  <div className="form-group">
                    <label htmlFor="kelas">Kelas</label>
                    <input
                      type="text"
                      id="kelas"
                      name="kelas"
                      value={formData.kelas}
                      onChange={handleInputChange}
                      placeholder="Contoh: X IPA 1"
                      className="animated-input"
                    />
                  </div>
                )}
              </div>
            </form>
          )}

          <div className="card-actions">
            {!editMode ? (
              <button
                className="btn btn-primary animated-btn"
                onClick={() => setEditMode(true)}
              >
                <span className="btn-icon">‚úèÔ∏è</span>
                Edit Profil
              </button>
            ) : (
              <div className="edit-actions">
                <button
                  type="button"
                  className="btn btn-secondary animated-btn"
                  onClick={() => {
                    setEditMode(false);
                    loadUserProfile();
                  }}
                >
                  <span className="btn-icon">‚ùå</span>
                  Batal
                </button>
                <button
                  type="submit"
                  className="btn btn-primary animated-btn"
                  onClick={handleSaveProfile}
                  disabled={saveLoading}
                >
                  <span className="btn-icon">{saveLoading ? '‚è≥' : 'üíæ'}</span>
                  {saveLoading ? 'Menyimpan...' : 'Simpan Perubahan'}
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Statistics Card */}
        <div className="stats-card interactive-card">
          <h3>Statistik Akun</h3>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-circle">
                <span className="stat-number">{animatedStats.classes}</span>
              </div>
              <div className="stat-label">Kelas Diikuti</div>
            </div>
            <div className="stat-card">
              <div className="stat-circle">
                <span className="stat-number">{animatedStats.assignments}</span>
              </div>
              <div className="stat-label">Tugas Diselesaikan</div>
            </div>
            <div className="stat-card">
              <div className="stat-circle">
                <span className="stat-number">{animatedStats.materials}</span>
              </div>
              <div className="stat-label">Materi Dipelajari</div>
            </div>
            <div className="stat-card">
              <div className="stat-circle">
                <span className="stat-number">{animatedStats.activeDays}</span>
              </div>
              <div className="stat-label">Hari Aktif</div>
            </div>
          </div>
        </div>

        {/* Achievements Card */}
        <div className="achievements-card interactive-card">
          <h3>Pencapaian</h3>
          <div className="achievements-grid">
            <div className="achievement-card unlocked">
              <div className="achievement-icon">üéØ</div>
              <div className="achievement-content">
                <h4>Pelajar Aktif</h4>
                <p>Login 7 hari berturut-turut</p>
              </div>
            </div>
            <div className="achievement-card unlocked">
              <div className="achievement-icon">üìö</div>
              <div className="achievement-content">
                <h4>Pembelajar</h4>
                <p>Menyelesaikan 10 tugas</p>
              </div>
            </div>
            <div className="achievement-card locked">
              <div className="achievement-icon">üèÜ</div>
              <div className="achievement-content">
                <h4>Master</h4>
                <p>Raih nilai sempurna 5 kali</p>
              </div>
            </div>
          </div>
        </div>

        {/* Activity Card */}
        <div className="activity-card interactive-card">
          <h3>Aktivitas Terbaru</h3>
          <div className="activity-list">
            <div className="activity-item">
              <div className="activity-icon">üìù</div>
              <div className="activity-content">
                <p>Mengerjakan tugas "Matematika Dasar"</p>
                <span className="activity-time">2 jam yang lalu</span>
              </div>
            </div>
            <div className="activity-item">
              <div className="activity-icon">üìö</div>
              <div className="activity-content">
                <p>Membaca materi "Fisika Modern"</p>
                <span className="activity-time">1 hari yang lalu</span>
              </div>
            </div>
            <div className="activity-item">
              <div className="activity-icon">‚úÖ</div>
              <div className="activity-content">
                <p>Menyelesaikan kuis "Bahasa Indonesia"</p>
                <span className="activity-time">3 hari yang lalu</span>
              </div>
            </div>
          </div>
        </div>

        {/* Settings Card */}
        <div className="settings-card interactive-card">
          <h3>Pengaturan Akun</h3>
          <div className="settings-grid">
            <div className="setting-card">
              <div className="setting-icon">üîî</div>
              <div className="setting-content">
                <h4>Notifikasi</h4>
                <p>Atur preferensi notifikasi</p>
                <button className="btn btn-outline">Konfigurasi</button>
              </div>
            </div>
            <div className="setting-card">
              <div className="setting-icon">üîí</div>
              <div className="setting-content">
                <h4>Keamanan</h4>
                <p>Ubah kata sandi dan pengaturan keamanan</p>
                <button className="btn btn-outline">Ubah Password</button>
              </div>
            </div>
            <div className="setting-card">
              <div className="setting-icon">üé®</div>
              <div className="setting-content">
                <h4>Tampilan</h4>
                <p>Kustomisasi tampilan aplikasi</p>
                <button className="btn btn-outline">Tema</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profil;
