import React, { useState, useEffect } from 'react';
import api from '../services/api';
import '../styles/dashboard.css';

const AdminUserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createData, setCreateData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'siswa',
    nama_lengkap: '',
    kelas: ''
  });
  const [createLoading, setCreateLoading] = useState(false);
  const [createError, setCreateError] = useState('');

  useEffect(() => {
    fetchUsers(page);
  }, [page]);

  const fetchUsers = async (pageNum) => {
    try {
      setLoading(true);
      const res = await api.get('/admin/users', { params: { page: pageNum, limit: 10 } });
      setUsers(res.data.users);
      setTotalPages(res.data.pagination.totalPages || 1);
    } catch (err) {
      setError('Gagal memuat data pengguna');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCreateData(prev => ({ ...prev, [name]: value }));
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    setCreateError('');
    setCreateLoading(true);
    try {
      await api.post('/admin/users', createData);
      setShowCreateModal(false);
      setCreateData({
        username: '',
        email: '',
        password: '',
        role: 'siswa',
        nama_lengkap: '',
        kelas: ''
      });
      fetchUsers(page);
    } catch (err) {
      setCreateError(err.response?.data?.error || 'Gagal membuat pengguna');
      console.error(err);
    } finally {
      setCreateLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Apakah Anda yakin ingin menghapus pengguna ini?')) return;
    try {
      await api.delete('/admin/users/' + userId);
      fetchUsers(page);
    } catch (err) {
      alert('Gagal menghapus pengguna');
      console.error(err);
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Manajemen Pengguna Admin</h2>
      <button className="btn btn-primary" onClick={() => setShowCreateModal(true)}>
        Buat Pengguna Baru
      </button>

      {loading ? (
        <p>Memuat data pengguna...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          <table className="table">
            <thead>
              <tr>
                <th>Nama Lengkap</th>
                <th>Username</th>
                <th>Email</th>
                <th>Role</th>
                <th>Kelas</th>
                <th>Aksi</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.nama_lengkap}</td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                  <td>{user.kelas || '-'}</td>
                  <td>
                    <button className="btn btn-danger" onClick={() => handleDeleteUser(user.id)}>
                      Hapus
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination">
            <button disabled={page <= 1} onClick={() => setPage(page - 1)}>
              Sebelumnya
            </button>
            <span>Halaman {page} dari {totalPages}</span>
            <button disabled={page >= totalPages} onClick={() => setPage(page + 1)}>
              Berikutnya
            </button>
          </div>
        </>
      )}

      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Buat Pengguna Baru</h3>
            {createError && <p className="error-message">{createError}</p>}
            <form onSubmit={handleCreateUser}>
              <div className="form-group">
                <label>Nama Lengkap</label>
                <input
                  type="text"
                  name="nama_lengkap"
                  value={createData.nama_lengkap}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  name="username"
                  value={createData.username}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={createData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  value={createData.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Role</label>
                <select name="role" value={createData.role} onChange={handleInputChange} required>
                  <option value="siswa">Siswa</option>
                  <option value="guru">Guru</option>
                </select>
              </div>
              {createData.role === 'siswa' && (
                <div className="form-group">
                  <label>Kelas (opsional)</label>
                  <input
                    type="text"
                    name="kelas"
                    value={createData.kelas}
                    onChange={handleInputChange}
                  />
                </div>
              )}
              <div className="modal-actions">
                <button type="button" className="btn btn-secondary" onClick={() => setShowCreateModal(false)}>
                  Batal
                </button>
                <button type="submit" className="btn btn-primary" disabled={createLoading}>
                  {createLoading ? 'Membuat...' : 'Buat'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminUserManagement;
