import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { assignmentsAPI } from '../services/api';
import '../styles/dashboard.css';

const DetailTugas = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [assignment, setAssignment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAssignment();
  }, [id]);

  const fetchAssignment = async () => {
    try {
      setLoading(true);
      const response = await assignmentsAPI.getById(id);
      setAssignment(response.data);
    } catch (error) {
      setError('Gagal memuat detail tugas');
      console.error('Error fetching assignment details:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Memuat detail tugas...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-container">
        <div className="error-message">{error}</div>
        <button className="btn btn-secondary" onClick={() => navigate(-1)}>
          Kembali
        </button>
      </div>
    );
  }

  if (!assignment) {
    return (
      <div className="dashboard-container">
        <div className="error-message">Tugas tidak ditemukan</div>
        <button className="btn btn-secondary" onClick={() => navigate(-1)}>
          Kembali
        </button>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <button className="btn btn-secondary" onClick={() => navigate(-1)} style={{ marginBottom: '10px' }}>
          ‚Üê Kembali
        </button>
        <h1>{assignment.judul}</h1>
        <p>Kode: {assignment.kode_tugas || 'N/A'}</p>
        <p>Guru: {assignment.guru_nama || 'N/A'}</p>
        <p><strong>Deadline:</strong> {new Date(assignment.deadline).toLocaleDateString('id-ID')}</p>
        <p><strong>Dibuat:</strong> {new Date(assignment.created_at).toLocaleDateString('id-ID')}</p>
        {assignment.deskripsi && <p>{assignment.deskripsi}</p>}
      </div>
    </div>
  );
};

export default DetailTugas;
