import React, { useState, useEffect } from 'react';
import { classesAPI, materialsAPI, assignmentsAPI } from '../services/api';
import '../styles/dashboard.css';

const DashboardAdmin = () => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalClasses: 0,
    totalMaterials: 0,
    totalAssignments: 0,
    totalSubmissions: 0,
    userStats: []
  });
  const [recentUsers, setRecentUsers] = useState([]);
  const [recentClasses, setRecentClasses] = useState([]);
  const [recentMaterials, setRecentMaterials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showUserManagement, setShowUserManagement] = useState(false);
  const [users, setUsers] = useState([]);
  const [userLoading, setUserLoading] = useState(false);
  const [userError, setUserError] = useState('');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      // For now, we'll use existing APIs to get some data
      // Later this will be replaced with admin-specific API calls
      const [classesRes, materialsRes, assignmentsRes] = await Promise.all([
        classesAPI.getAll(),
        materialsAPI.getRecent(),
        assignmentsAPI.getUpcoming()
      ]);

      // Mock admin stats for now
      setStats({
        totalUsers: 150, // This would come from admin API
        totalClasses: classesRes.data.length,
        totalMaterials: materialsRes.data.length,
        totalAssignments: assignmentsRes.data.length,
        totalSubmissions: 0, // This would come from admin API
        userStats: [
          { role: 'siswa', count: 120 },
          { role: 'guru', count: 25 },
          { role: 'admin', count: 5 }
        ]
      });

      setRecentClasses(classesRes.data.slice(0, 5));
      setRecentMaterials(materialsRes.data.slice(0, 5));

      // Mock recent users
      setRecentUsers([
        { id: 1, username: 'john_doe', nama_lengkap: 'John Doe', role: 'siswa', created_at: new Date() },
        { id: 2, username: 'jane_smith', nama_lengkap: 'Jane Smith', role: 'guru', created_at: new Date() },
        { id: 3, username: 'admin_user', nama_lengkap: 'Admin User', role: 'admin', created_at: new Date() }
      ]);

    } catch (error) {
      setError('Failed to fetch dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      setUserLoading(true);
      // This would be replaced with admin API call
      setUsers([
        { id: 1, username: 'john_doe', email: 'john@example.com', role: 'siswa', nama_lengkap: 'John Doe', created_at: new Date() },
        { id: 2, username: 'jane_smith', email: 'jane@example.com', role: 'guru', nama_lengkap: 'Jane Smith', created_at: new Date() },
        { id: 3, username: 'admin_user', email: 'admin@example.com', role: 'admin', nama_lengkap: 'Admin User', created_at: new Date() }
      ]);
    } catch (error) {
      setUserError('Failed to fetch users');
      console.error('Error fetching users:', error);
    } finally {
      setUserLoading(false);
    }
  };

  const handleUserManagement = () => {
    setShowUserManagement(true);
    fetchUsers();
  };

  if (loading) {
    return <div className="loading">Loading admin dashboard...</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Dashboard Admin</h1>
        <p>Selamat datang di panel administrasi e-learning SMA</p>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="dashboard-grid">
        {/* System Statistics */}
        <div className="card">
          <h2>Statistik Sistem</h2>
          <div className="stats-grid">
            <div className="stat-item">
              <h3>{stats.totalUsers}</h3>
              <p>Total Pengguna</p>
            </div>
            <div className="stat-item">
              <h3>{stats.totalClasses}</h3>
              <p>Total Kelas</p>
            </div>
            <div className="stat-item">
              <h3>{stats.totalMaterials}</h3>
              <p>Total Materi</p>
            </div>
            <div className="stat-item">
              <h3>{stats.totalAssignments}</h3>
              <p>Total Tugas</p>
            </div>
          </div>
        </div>

        {/* User Statistics */}
        <div className="card">
          <h2>Statistik Pengguna</h2>
          {stats.userStats.map((stat) => (
            <div key={stat.role} className="user-stat">
              <span className="role-label">{stat.role.charAt(0).toUpperCase() + stat.role.slice(1)}</span>
              <span className="count">{stat.count}</span>
            </div>
          ))}
        </div>

        {/* Recent Users */}
        <div className="card">
          <h2>Pengguna Terbaru</h2>
          {recentUsers.length === 0 ? (
            <p>Tidak ada pengguna terbaru</p>
          ) : (
            <div>
              {recentUsers.map((user) => (
                <div key={user.id} className="user-card">
                  <h3>{user.nama_lengkap}</h3>
                  <p>Username: {user.username}</p>
                  <p>Role: {user.role}</p>
                  <small>Dibuat: {new Date(user.created_at).toLocaleDateString('id-ID')}</small>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Recent Classes */}
        <div className="card">
          <h2>Kelas Terbaru</h2>
          {recentClasses.length === 0 ? (
            <p>Tidak ada kelas terbaru</p>
          ) : (
            <div>
              {recentClasses.map((classItem) => (
                <div key={classItem.id} className="class-card">
                  <h3>{classItem.nama_kelas}</h3>
                  <p>Kode: {classItem.kode_kelas}</p>
                  <p>Guru: {classItem.guru_nama}</p>
                  {classItem.deskripsi && <p>{classItem.deskripsi}</p>}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Recent Materials */}
        <div className="card">
          <h2>Materi Terbaru</h2>
          {recentMaterials.length === 0 ? (
            <p>Tidak ada materi terbaru</p>
          ) : (
            <div>
              {recentMaterials.map((material) => (
                <div key={material.id} className="material-card">
                  <h3>{material.judul}</h3>
                  <p>Kelas: {material.nama_kelas}</p>
                  <p>Oleh: {material.created_by_name}</p>
                  {material.deskripsi && <p>{material.deskripsi}</p>}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <div className="card">
          <h2>Quick Actions</h2>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            <button
              className="btn btn-primary"
              onClick={handleUserManagement}
            >
              Kelola Pengguna
            </button>
            <button className="btn btn-primary">Laporan Sistem</button>
            <button className="btn btn-primary">Pengaturan Sistem</button>
          </div>
        </div>
      </div>

      {/* User Management Modal */}
      {showUserManagement && (
        <div className="modal-overlay">
          <div className="modal" style={{ maxWidth: '1000px' }}>
            <h2>Kelola Pengguna</h2>
            {userError && <div className="error">{userError}</div>}

            {userLoading ? (
              <div className="loading">Memuat data pengguna...</div>
            ) : (
              <div>
                <div className="user-list">
                  {users.map((user) => (
                    <div key={user.id} className="user-item">
                      <div>
                        <strong>{user.nama_lengkap}</strong>
                        <p>Username: {user.username}</p>
                        <p>Email: {user.email}</p>
                        <p>Role: {user.role}</p>
                        <small>Dibuat: {new Date(user.created_at).toLocaleDateString('id-ID')}</small>
                      </div>
                      <div style={{ display: 'flex', gap: '10px' }}>
                        <select
                          value={user.role}
                          onChange={(e) => {
                            // Handle role change
                            console.log(`Change role for user ${user.id} to ${e.target.value}`);
                          }}
                          className="form-select"
                        >
                          <option value="siswa">Siswa</option>
                          <option value="guru">Guru</option>
                          <option value="admin">Admin</option>
                        </select>
                        <button className="btn btn-danger btn-sm">Hapus</button>
                      </div>
                    </div>
                  ))}
                </div>

                <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '20px' }}>
                  <button
                    className="btn btn-secondary"
                    onClick={() => setShowUserManagement(false)}
                  >
                    Tutup
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Modal Styles */}
      <style>
        {`
          .stats-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
            gap: 15px;
            margin-top: 15px;
          }

          .stat-item {
            text-align: center;
            padding: 15px;
            background-color: #f8f9fa;
            border-radius: 8px;
            border: 1px solid #e9ecef;
          }

          .stat-item h3 {
            margin: 0 0 5px 0;
            color: #007bff;
            font-size: 24px;
          }

          .stat-item p {
            margin: 0;
            color: #6c757d;
            font-size: 14px;
          }

          .user-stat {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: 10px 0;
            border-bottom: 1px solid #e9ecef;
          }

          .user-stat:last-child {
            border-bottom: none;
          }

          .role-label {
            font-weight: bold;
            text-transform: capitalize;
          }

          .count {
            background-color: #007bff;
            color: white;
            padding: 2px 8px;
            border-radius: 12px;
            font-size: 12px;
          }

          .user-card, .class-card, .material-card {
            border: 1px solid #e9ecef;
            border-radius: 8px;
            padding: 15px;
            margin-bottom: 10px;
            background-color: #f8f9fa;
          }

          .user-card h3, .class-card h3, .material-card h3 {
            margin: 0 0 8px 0;
            color: #007bff;
          }

          .user-card p, .class-card p, .material-card p {
            margin: 5px 0;
            color: #6c757d;
          }

          .modal-overlay {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-color: rgba(0, 0, 0, 0.5);
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 1000;
          }

          .modal {
            background: white;
            padding: 30px;
            border-radius: 8px;
            width: 90%;
            max-width: 500px;
            max-height: 80vh;
            overflow-y: auto;
          }

          .user-list {
            margin: 15px 0;
          }

          .user-item {
            border: 1px solid #ddd;
            border-radius: 5px;
            padding: 15px;
            margin-bottom: 10px;
            display: flex;
            justify-content: space-between;
            align-items: center;
          }

          .user-item:hover {
            background-color: #f8f9fa;
          }

          .form-select {
            padding: 5px 10px;
            border: 1px solid #ddd;
            border-radius: 4px;
            background-color: white;
          }

          .btn-sm {
            padding: 5px 10px;
            font-size: 12px;
          }

          .btn-danger {
            background-color: #dc3545;
            border-color: #dc3545;
            color: white;
          }

          .btn-danger:hover {
            background-color: #c82333;
            border-color: #bd2130;
          }
        `}
      </style>
    </div>
  );
};

export default DashboardAdmin;
