import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';
import '../styles/dashboard.css';

const roles = ['siswa', 'guru', 'admin'];

const AdminUserManagement = () => {
  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createData, setCreateData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'siswa',
    nama_lengkap: '',
  });
  const [createError, setCreateError] = useState('');
  const [createLoading, setCreateLoading] = useState(false);
  const [editUserId, setEditUserId] = useState(null);
  const [editRole, setEditRole] = useState('');
  const [editLoading, setEditLoading] = useState(false);
  const [deleteUserId, setDeleteUserId] = useState(null);
  const [deleteLoading, setDeleteLoading] = useState(false);

  useEffect(() => {
    fetchUsers(page);
  }, [page]);

  const fetchUsers = async (pageNum) => {
    setLoading(true);
    setError('');
    try {
      const res = await adminAPI.getAllUsers(pageNum, 10);
      console.log('Fetch users response:', res);
      if (res.data && res.data.users) {
        setUsers(res.data.users);
        setTotalPages(res.data.pagination.totalPages);
      } else {
        setError('Data pengguna tidak ditemukan dalam respons API');
      }
    } catch (err) {
      console.error('Error fetching users:', err);
      setError(err.response?.data?.error || 'Gagal memuat data pengguna');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateInputChange = (e) => {
    setCreateData({ ...createData, [e.target.name]: e.target.value });
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    setCreateError('');
    setCreateLoading(true);
    try {
      await adminAPI.createUser(createData);
      setShowCreateModal(false);
      setCreateData({
        username: '',
        email: '',
        password: '',
        role: 'siswa',
        nama_lengkap: '',
      });
      fetchUsers(page);
    } catch (err) {
      setCreateError(err.response?.data?.error || 'Gagal membuat pengguna');
    } finally {
      setCreateLoading(false);
    }
  };

  const startEditRole = (user) => {
    setEditUserId(user.id);
    setEditRole(user.role);
  };

  const cancelEditRole = () => {
    setEditUserId(null);
    setEditRole('');
  };

  const saveEditRole = async (userId) => {
    setEditLoading(true);
    try {
      await adminAPI.updateUserRole(userId, { role: editRole });
      setEditUserId(null);
      setEditRole('');
      fetchUsers(page);
    } catch (err) {
      setError('Gagal memperbarui peran pengguna');
    } finally {
      setEditLoading(false);
    }
  };

  const confirmDeleteUser = (userId) => {
    setDeleteUserId(userId);
  };

  const cancelDeleteUser = () => {
    setDeleteUserId(null);
  };

  const deleteUser = async () => {
    setDeleteLoading(true);
    try {
      await adminAPI.deleteUser(deleteUserId);
      setDeleteUserId(null);
      fetchUsers(page);
    } catch (err) {
      setError('Gagal menghapus pengguna');
    } finally {
      setDeleteLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Manajemen Pengguna</h2>
      <button className="btn btn-primary" onClick={() => setShowCreateModal(true)}>
        Buat Pengguna Baru
      </button>

      {error && <div className="error-message">{error}</div>}

      <div className="users-table-container">
        {loading ? (
          <div className="loading">Memuat data pengguna...</div>
        ) : (
          <table className="users-table">
            <thead>
              <tr>
                <th>Nama Lengkap</th>
                <th>Username</th>
                <th>Email</th>
                <th>Role</th>
                <th>Dibuat Pada</th>
                <th>Aksi</th>
              </tr>
            </thead>
            <tbody>
              {users.length === 0 && (
                <tr>
                  <td colSpan="6" style={{ textAlign: 'center' }}>
                    Tidak ada pengguna ditemukan.
                  </td>
                </tr>
              )}
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.nama_lengkap}</td>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    {editUserId === user.id ? (
                      <>
                        <select
                          value={editRole}
                          onChange={(e) => setEditRole(e.target.value)}
                          disabled={editLoading}
                        >
                          {roles.map((r) => (
                            <option key={r} value={r}>
                              {r.charAt(0).toUpperCase() + r.slice(1)}
                            </option>
                          ))}
                        </select>
                        <button
                          className="btn btn-small btn-primary"
                          onClick={() => saveEditRole(user.id)}
                          disabled={editLoading}
                        >
                          Simpan
                        </button>
                        <button
                          className="btn btn-small btn-secondary"
                          onClick={cancelEditRole}
                          disabled={editLoading}
                        >
                          Batal
                        </button>
                      </>
                    ) : (
                      <>
                        <span className={`role-badge ${user.role}`}>{user.role}</span>
                        <button
                          className="btn btn-small btn-secondary"
                          onClick={() => startEditRole(user)}
                        >
                          Ubah
                        </button>
                      </>
                    )}
                  </td>
                  <td>{new Date(user.created_at).toLocaleDateString()}</td>
                  <td>
                    <button
                      className="btn btn-small btn-danger"
                      onClick={() => confirmDeleteUser(user.id)}
                    >
                      Hapus
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div className="pagination">
        <button
          className="btn btn-small"
          onClick={() => setPage((p) => Math.max(p - 1, 1))}
          disabled={page === 1}
        >
          Sebelumnya
        </button>
        <span>
          Halaman {page} dari {totalPages}
        </span>
        <button
          className="btn btn-small"
          onClick={() => setPage((p) => Math.min(p + 1, totalPages))}
          disabled={page === totalPages}
        >
          Berikutnya
        </button>
      </div>

      {/* Create User Modal */}
      {showCreateModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Buat Pengguna Baru</h2>
            {createError && <div className="error-message">{createError}</div>}
            <form onSubmit={handleCreateUser}>
              <div className="form-group">
                <label htmlFor="nama_lengkap">Nama Lengkap:</label>
                <input
                  type="text"
                  id="nama_lengkap"
                  name="nama_lengkap"
                  value={createData.nama_lengkap}
                  onChange={handleCreateInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="username">Username:</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={createData.username}
                  onChange={handleCreateInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={createData.email}
                  onChange={handleCreateInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={createData.password}
                  onChange={handleCreateInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="role">Role:</label>
                <select
                  id="role"
                  name="role"
                  value={createData.role}
                  onChange={handleCreateInputChange}
                  required
                >
                  {roles.map((r) => (
                    <option key={r} value={r}>
                      {r.charAt(0).toUpperCase() + r.slice(1)}
                    </option>
                  ))}
                </select>
              </div>
              <div className="modal-actions">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowCreateModal(false)}
                  disabled={createLoading}
                >
                  Batal
                </button>
                <button type="submit" className="btn btn-primary" disabled={createLoading}>
                  {createLoading ? 'Menyimpan...' : 'Buat'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {deleteUserId && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Konfirmasi Hapus Pengguna</h2>
            <p>Apakah Anda yakin ingin menghapus pengguna ini?</p>
            <div className="modal-actions">
              <button
                className="btn btn-secondary"
                onClick={cancelDeleteUser}
                disabled={deleteLoading}
              >
                Batal
              </button>
              <button
                className="btn btn-danger"
                onClick={deleteUser}
                disabled={deleteLoading}
              >
                {deleteLoading ? 'Menghapus...' : 'Hapus'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminUserManagement;
